INSERT INTO `letyii_article` VALUES ('5', 'Mộng Tiên - Game bom tấn 2013 sắp ra mắt', '', '', '<div>Mộng Tiên - Webgame bom tấn của năm 2013 sắp ra mắt. Đây là tựa game MMORPG có cốt truyện hấp dẫn, đậm chất sử thi, được xây dựng công phu và logic, dựa trên những câu chuyện thần thoại, cổ tích về Người – Quỷ - Tiên. Khác với nhiều webgame 2D hiện nay có giao diện, màu sắc, lòe loẹt, Mộng Tiên có hệ thống đồ họa được thiết kế 3D rõ ràng, các chi tiết sắc nét, nhân vật được tạo hình bắt mắt. Đặc biệt là khung cảnh trong game. Ngoài ra, Mộng Tiên sẽ sở hữu lối ứng chiến hấp dẫn, độ pk cao, mang tính cạnh tranh quyết liệt, đảm bảo  yếu tố công bằng giữa các người chơi cùng với đó là hệ thống skill và item cực kì phong phú. Dự kiến game sẽ ra mắt trong dịp cuối năm nay </div>  ', '', '', '', null, null, null, null, null, '50324', '2013-11-27 10:26:08', '50324', '2013-12-12 10:36:32', '0', '1');
INSERT INTO `letyii_article` VALUES ('6', 'Băng Long Động', '', '', '<p style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Phụ bản\r\nBằng Long Động là nơi cho các đạo hữu thể hiện được bản lĩnh của mình, vượt qua\r\ncác cửa ải sẽ nhận được những phần thưởng hấp dẫn và một lượng lớn kinh nghiệm.</p>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\"><strong>Điều\r\nkiện</strong></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">•&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Điều kiện tham gia: Người chơi có đẳng\r\ncấp từ 20 trở lên.</div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">•&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Mỗi ngày có thể vào phó bản tối đa 3\r\nlần.</div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\"><strong>Cách vào\r\nphó bản</strong></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Mỗi ngày\r\nngười chơi có thể đến gặp NPC Thiên Cơ Tử ở Trừ Tà Thành mỗi nước để vào phó\r\nbản Bằng Long Động.</div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif;text-align:center\"><img src=\"/uploads/banglangdong1.jpg\" style=\"width:261px;height:287px;vertical-align:middle\" /><br /></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Sau khi\r\nđối thoại với NPC, người chơi sẽ được thông báo số lần đã vào phó bản trong\r\nngày, nhấp vào chữ Vào để được hệ thống đưa vào phó bản.</div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif;text-align:center\"><img src=\"/uploads/banglangdong2.jpg\" style=\"width:484px;height:208px\" /><br /></div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif\"><br /></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\"><strong>Khiêu\r\nchiến phó bản</strong></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Trong\r\nBăng Long Động không hạn chế thời gian, người chơi có thể thám hiểm bao lâu tùy\r\nthích, nơi đây chủ yếu để người chơi làm quen với phó bản.</div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif;text-align:center\"><img src=\"/uploads/banglangdong3.jpg\" style=\"width:500px;height:294px\" /><br /></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Người\r\nchơi cần tiêu diệt tất cả các loại quái trong phó bản để có cơ hội nhận được\r\ncác loại vật phẩm như: trang bị xanh các loại cấp 20, đá tẩy luyện lam, đá\r\ncường hoá level 1, rượu Quỳnh Tương, củi …</div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif;text-align:center\"><img src=\"/uploads/tientunglam4.jpg\" style=\"width:221px;height:176px\" /><br /></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Đây là\r\nphó bản dễ, phù hợp cho người chơi ở cấp độ thấp có cơ hội tìm kiếm đầy đủ các\r\nloại trang bị cấp thấp để trang bị cho bản thân.</div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Sau khi\r\nvượt ải thành công, người chơi sẽ được hệ thống tự động đánh giá chiến tích và\r\nnhận được một lần quay bàn cổ phó bản, nhận được thêm 1 loại vật phẩm.</div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif;text-align:center\"><img src=\"/uploads/banglangdong5.jpg\" style=\"width:500px;height:216px\" /><br /></div>\r\n<div style=\"font-size:medium;font-family:\'times new roman\', times, serif\">Tuỳ theo\r\nloại phó bản nào, mà sau khi hoàn thành phó bản xong bàn cổ của phó bản sẽ có\r\nphần thưởng tặng thêm tương ứng. Nếu sau khoảng thời gian 1 phút, người chơi\r\nkhông mở bàn cổ phó bản, bàn cổ tự động mất.</div><div style=\"font-size:medium;font-family:\'times new roman\', times, serif;text-align:center\"><img src=\"/uploads/banglangdong6.jpg\" style=\"width:325px;height:339px\" /><br /></div>  ', '', '', '', null, null, null, null, null, '50324', '2013-11-29 11:40:14', '50324', '2013-11-29 15:07:56', '0', '1');
INSERT INTO `letyii_article` VALUES ('7', 'Tiên Tung Lâm', '', '', '<div>Trong trận chiến giữa người và tiên,\r\nHồng Quân Lão Tổ đã dùng Tạo Hóa Ngọc Điệp để mở ra không gian này, là nơi bảo\r\nvệ Hồng Hoang Dị Thú và là nơi tụ họp của yêu mà tà đạo. Nơi đây ẩn chứa rất\r\nnhiều thần binh tiên khí, thích hợp là nơi thử sức của các bậc tu chân, đánh\r\nbái quái vật trong phó bản sẽ nhận được phần thưởng hậu hĩnh.</div>\r\n<div><strong>Điều kiện</strong></div>\r\n<div>Điều kiện tham gia: Người chơi có đẳng\r\ncấp từ 30 trở lên</div>\r\n<div>Số lần tham gia phó bản của hôm đó vẫn\r\ncòn.</div>\r\n<div>Phó bản có 2 dạng khiêu chiến là Khiêu\r\nchiến cá nhân và Khiêu chiến tinh anh (tổ đội), mỗi loại mỗi ngày có thể tham\r\ngia 3 lần.</div>\r\n<div><strong>Cách tham gia</strong></div>\r\n<div>Mỗi ngày người chơi đến Thái Cơ Thành\r\ntìm Tiên Tung Tôn Giả (177,158), đối thoại với NPC để vào phó bản Tiên Tung Lâm\r\ntheo dạng đơn hoặc NPC Sứ Giả Tiên Tung (145,180) để vào phó bản theo dạng tinh\r\nanh.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tientunglam1.jpg\" style=\"width:277px;height:268px\" /><br /></div><div style=\"text-align:center\">NPC Tiên Trung Tôn Giả Đơn</div><div style=\"text-align:center\"><br /></div><div style=\"text-align:center\"><img src=\"/uploads/tientunglam2.jpg\" style=\"width:264px;height:280px\" /><br /></div><div style=\"text-align:center\">NPC Sứ Giả Tiên Trung</div>\r\n<div><strong>Khiêu chiến phó bản</strong></div>\r\n<div>Sau khi đối thoại với 1 trong 2 NPC để\r\nvào phó bản, người chơi sẽ được hệ thống đưa vào bản đồphó bản Tiên Tung Lâm.\r\nĐây là bản đồ chung cho cả 2 loại khiêu chiến phó bản đơn và tinh anh.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tientunglam3.jpg\" style=\"width:500px;height:296px\" /><br /></div>\r\n<div style=\"text-align:center\">Phó bản Tiên Tung Lâm</div><div style=\"text-align:center\"><br /></div>\r\n<div>Người chơi vào phó bản cần tiêu diệt các\r\nloại quái trong phó bản để có cơ hội nhận được các loại phần thưởng như: sách\r\nkỹ năng, trang bị xanh cấp 30, Trừ tà lệnh, Ảo hoá đơn, đá tẩy luyện…</div><div style=\"text-align:center\"><img src=\"/uploads/tientunglam4.jpg\" style=\"width:221px;height:176px\" /><br /></div>\r\n<div style=\"text-align:center\">Phần thưởng có thể nhận</div>\r\n<div>Đây là phó bản đầu tiên người chơi có\r\nthể tham gia khi đặt được đẳng cấp 30. Với cơ hội 1 ngày có thể tham gia tối đa\r\n6 lần, người chơi sẽ dễ dàng hơn trong việc tìm kiếm các loại trang bị và sách\r\nkỹ năng cho bản thân. Sau khi vượt ải thành công, người chơi sẽ được hệ thống\r\ntự động đánh giá chiến tích và nhận được một lần quay bàn cổ phó bản, nhận được\r\nthêm 1 loại vật phẩm.</div>\r\n<div style=\"text-align:center\">&nbsp;<img src=\"/uploads/tientunglam6.jpg\" style=\"width:500px;height:216px\" /></div><div style=\"text-align:center\">Đánh giá chiến tích</div><div style=\"text-align:center\"><br /></div>\r\n<div>Tuỳ theo loại phó bản nào, mà sau khi\r\nhoàn thành phó bản xong bàn cổ của phó bản sẽ có phần thưởng tặng thêm tương\r\nứng. Nếu sau khoảng thời gian 1 phút, người chơi không mở bàn cổ phó bản, bàn\r\ncổ tự động mất.</div><div style=\"text-align:center\"><img src=\"/uploads/tientunglam7.jpg\" style=\"width:328px;height:327px\" /><br /></div>\r\n<p>&nbsp;</p>  ', '', '', '', null, null, null, null, null, '50324', '2013-11-29 12:36:20', '50324', '2013-11-29 15:07:45', '0', '1');
INSERT INTO `letyii_article` VALUES ('8', 'Đảo Thú Cưng', '', '', '<div>Có một hòn đảo ẩn chứa nhiều loại sủng\r\nvật quý hiếm, tham gia phó bản người chơi sẽ khám phá được thế giới thú cưng đa\r\ndạng, nhận những phần thưởng hấp dẫn. Sủng Vật Đảo sẽ là một trải nghiệm khó có\r\nthể bỏ qua trong thế giới Tiên Hiệp Truyền Kỳ 2.</div>\r\n<div><strong>Điều kiện</strong></div>\r\n<div>Điều kiện tham gia: Người chơi có đẳng\r\ncấp từ 30 trở lên</div>\r\n<div>Mỗi ngày có thể vào phó bản 3 lần.</div>\r\n<div>Cách tham gia</div>\r\n<div>Người chơi đến Thái Cơ Thành tìm NPC\r\nTiên Thú Lãnh Tuyết (142,239) đối thoại với NPC để có thể vào phó bản.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/daothucung1.jpg\" style=\"width:256px;height:309px\" /><br /></div>\r\n<div><strong>Khiêu chiến phó bản</strong></div>\r\n<div>Trong phó bản có rất nhiều loại quái,\r\nđánh bại quái sẽ nhận được trứng pet có hình dạng giống quái đánh bại. Mỗi lần\r\nvào phó bản thì quái trong đó sẽ làm mới lại.</div><div><br /></div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/daothucung2.jpg\" style=\"width:419px;height:252px\" /><br /></div>\r\n<div style=\"text-align:center\">Phó bản Đảo Thú Cưng</div>\r\n<div>Người chơi cần tiêu diệt hết các loại\r\nquái trong bản đồ để có thể nhận được các loại trứng thú cưng phẩm chất khác\r\nnhau. Khi tham chiến sẽ có cơ hội gặp Thần thú, đánh bại Thần thú sẽ có xác\r\nsuất nhận được trứng thú cưng phẩm chất cao.</div><div><br /></div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/daothucung3.jpg\" style=\"width:225px;height:185px\" /><br /></div>\r\n<div><br /></div><div>Đây là phó bản duy nhất mà phần thưởng\r\nlà các loại trứng thú cưng, giúp người chơi có cơ hội sở hữu các loại trứng thú\r\ncưng phù hợp.</div>\r\n<div>Sau khi vượt ải thành công, người chơi\r\nsẽ được hệ thống tự động đánh giá chiến tích và nhận được một lần quay bàn cổ\r\nphó bản, nhận được thêm 1 loại vật phẩm</div>\r\n<div>&nbsp;</div><div style=\"text-align:center\"><img src=\"/uploads/daothucung4.jpg\" style=\"width:500px;height:216px\" /><br /></div>\r\n<div style=\"text-align:center\">Đánh giá chiến tích</div>\r\n<div>Tuỳ theo loại phó bản nào, mà sau khi\r\nhoàn thành phó bản xong bàn cổ của phó bản sẽ có phần thưởng tặng thêm tương\r\nứng. Nếu sau khoảng thời gian 1 phút, người chơi không mở bàn cổ phó bản, bàn\r\ncổ tự động mất.</div>\r\n<div>&nbsp;</div><div style=\"text-align:center\"><img src=\"/uploads/daothucung5.jpg\" style=\"width:321px;height:328px\" /><br /></div>\r\n<div style=\"text-align:center\">Linh Sủng Bàn</div>  ', '', '', '', null, null, null, null, null, '50324', '2013-11-29 15:19:57', '50324', '2013-11-29 15:20:03', '0', '1');
INSERT INTO `letyii_article` VALUES ('9', 'Nữ Oa bí cảnh', '', '', '<div>Đỉnh núi phía Nam bỗng xuất hiện một vùng đất kỳ bí, tương truyền là nơi ở\r\ncủa Nữ Oa khi lấy đá vá trời, khi các bậc tu đạo hậu thế bước vào đã phát hiện\r\nra tuy bên trong không còn thần thạch, nhưng vẫn có rất nhiều bảo vật, biết\r\nđược điều này, rất nhiều người đã vào thăm dò.</div><div><strong><br /></strong></div><div><strong>Điều kiện vào\r\nphó bản</strong></div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<strong>Điều kiện:&nbsp;</strong><strong>Nhân vật đạt từ cấp độ 30 trở lên.</strong></div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nMỗi ngày có thể vào phó bản 2 lần.</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nBất cứ thời gian nào trong ngày, người chơi cũng có thể đến gặp&nbsp;<strong>NPC\r\nBí cảnh Nữ Oa (Thái Cơ Thành 230,145)</strong>&nbsp;đối thoại để có thể vào phó bản</div><div><br /></div><div style=\"text-align:center\"><img src=\"/uploads/nuoa1.jpg\" style=\"width:276px;height:270px\" /><br /></div><div>.</div>\r\n<div><strong>Quy tắc phó\r\nbản:</strong></div>\r\n<div>Phó bản có thời gian quy định là 15 phút, trong 15 phút người chơi cần đánh\r\nbại các quái trong bản đồ để tích luỹ số đồng khoá/đồng sẽ được nhận sau khi\r\nphó bản kết thúc.</div>\r\n<div><br /></div><div style=\"text-align:center\"><img src=\"/uploads/nuoa2.jpg\" style=\"width:500px;height:301px\" /><br /></div><div><br /></div>\r\n<div>Sau khi vào phó bản, có thể tích lũy số lần đánh bại liên tiếp các quái vật\r\n(liên trảm) để &nbsp;nhận được thêm các hiệu ứng gia tăng sát thương của bản\r\nthân, từ đó dễ dàng hơn trong việc đánh bại boss ở giữa.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/nuoa3.jpg\" style=\"width:300px;height:349px\" /><br /></div>\r\n<div style=\"text-align:center\">Hiệu ứng nhận được khi tích luỹ liên\r\ntrảm</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nMỗi lần đánh bại boss sẽ có cơ hội quay số ra các đồng tiền, cần nhặt càng\r\nnhiều càng tốt trong thời gian quy định.</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nNếu đang đánh quái mà bị tử vong hoặc gián đoạn thì số lần liên trảm sẽ\r\ntính lại từ đầu.</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nTrong quá trình tham gia phó bản, người chơi có thể thoát ra ngoài bất cứ\r\nlúc nào qua nút&nbsp;<strong>Lối ra</strong>&nbsp;phía trên bên phải màn hình, vẫn sẽ\r\nđược đánh giá chiến tích và nhận lần quay bàn cổ.</div>\r\n<div><strong><br /></strong></div><div><strong>Phần thưởng\r\nphó bản:</strong></div>\r\n<div>Ngoài việc nhặt được tiền sau khi đánh boss, sau khi rời phó bản, tùy vào\r\nsố lượng quái và boss đánh được sẽ nhận được thêm phần thưởng tiền. Khi đánh\r\nbại 1 Boss, sẽ nhận ngay một lượng đồng và có cơ hộ nhận được thêm lượng lớn\r\nđồng tuỳ thuộc vào lần quay nhận thưởng.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/nuoa4.jpg\" style=\"width:308px;height:220px\" /><br /></div>\r\n<div>Quay tiền sau khi đánh bại</div>\r\n<div>Sau khi bàn quay tiền dừng, người chơi có khoảng thời gian 1 phút để nhận\r\nthưởng đồng qua các đồng tiền vàng trên khắp bản đồ, số lượng tuỳ thuộc vào số\r\nbàn quay tiền.</div>\r\n<div>Ngoài ra trong phó bản, người chơi đánh bại các quái thường sẽ nhận được\r\nđồng khoá. Đánh bại càng nhiều quái, tích luỹ đồng khoá càng nhiều.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/nuoa5.jpg\" style=\"width:500px;height:277px\" /><br /></div>\r\n<div style=\"text-align:center\">Nhận thưởng đồng sau khi đánh bại Boss</div><div style=\"text-align:center\"><br /></div>\r\n<div>Đây là phó bản duy nhất hiện nay mang lại cho người chơi phần thưởng là\r\nlượng lớn đồng - đồng khoá, tạo hứng thú cao cho người chơi.</div>\r\n<div>Sau khi vượt ải thành công, người chơi sẽ được hệ thống tự động đánh giá\r\nchiến tích và nhận được một lần quay bàn cổ phó bản, nhận được thêm 1 loại vật\r\nphẩm</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/nuoa6.jpg\" /><br /></div>\r\n<div style=\"text-align:center\">Đánh giá chiến tích</div>\r\n<div>Tuỳ theo loại phó bản nào, mà sau khi hoàn thành phó bản xong bàn cổ của\r\nphó bản sẽ có phần thưởng tặng thêm tương ứng. Nếu sau khoảng thời gian 1 phút,\r\nngười chơi không mở bàn cổ phó bản, bàn cổ tự động mất.</div>\r\n<div>&nbsp;</div><div style=\"text-align:center\"><img src=\"/uploads/nuoa7.jpg\" style=\"width:341px;height:333px\" /><br /></div>\r\n<div style=\"text-align:center\">Nữ Oa bàn cổ</div>  ', '', '', '', null, null, null, null, null, '50324', '2013-11-29 15:46:15', '50324', '2013-11-29 15:46:15', '0', '1');
INSERT INTO `letyii_article` VALUES ('10', 'Thập tuyệt tru tiên trận', '', '', '<div>Đại lục Thần Châu có&nbsp;<strong>36 Động Thiên 72 Phúc Địa, lại có Tứ Đại Tuyệt\r\nVực, Ngũ Đại Hung Địa, Thất Đại Tiên Cảnh,</strong>&nbsp;trong đó Tru Tiên Trận đứng\r\nđầu các Tuyệt Vực. Tương tuyền phó bản này do Thông Thiên Giáo Chủ sang tạo\r\nnên, bên trong có nhiều tầng trận pháp, với nhiều loại hình vượt trận kỳ dị,\r\nbiến ảo khôn lường.</div>\r\n<div><strong>Điều kiện</strong></div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<strong>Điều kiện tham gia:&nbsp;</strong><strong>Người chơi có đẳng cấp\r\ntừ 33 trở lên</strong>.</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nMỗi ngày có thể vào phó bản 2 lần.</div>\r\n<div><strong>Cách tham gia</strong></div>\r\n<div>Vào bất cứ thời gian nào trong ngày, người chơi có thể đến&nbsp;<strong>NPC Phá\r\nTrận Tử (Thái Cơ Thành 142, 152)&nbsp;</strong>đối thoại với NPC để có thể vào khiêu\r\nchiến phó bản.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran1.jpg\" style=\"width:232px;height:259px\" />&nbsp;</div>\r\n<div style=\"text-align:center\">NPC Phá Trận Tử</div>\r\n<div>Trong quá trình tham gia phó bản, người chơi có thể thoát ra ngoài bất cứ\r\nlúc nào qua nút<strong>&nbsp;Lối ra</strong>phía trên bên phải màn hình, vẫn sẽ được đánh\r\ngiá chiến tích và nhận lần quay bàn cổ.</div>\r\n<div><strong>Khiêu chiến\r\nphó bản</strong></div>\r\n<div>Phó bản&nbsp;<strong>Thập Tuyệt Tru Tiên Trận</strong>&nbsp;có tổng cộng 28 tầng chưa\r\ntính các tầng ẩn và các tầng thần bí, người chơi vào phó bản sẽ tuần tự vượt\r\ncác tầng từ thấp đến cao với độ khó tăng dần lên và thời gian vượt tầng rút\r\nngắn lại, tạo thử thách cho người chơi tham gia khiêu chiến phó bản.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran2.jpg\" style=\"width:500px;height:296px\" /><br /></div>\r\n<div style=\"text-align:center\">Tru Tiên Trận thần bí</div>\r\n<div>Ở tầng thần bí, người chơi sẽ có cơ hội mua được các vật phẩm kỳ trân dị\r\nbảo không thể tìm kiếm được ở bản đồ bên ngoài, và các vật phẩm ở tiệm thương\r\nnhân thần bí sẽ thay đổi tuỳ theo tầng người chơi vượt được trong phó bản.</div><div style=\"text-align:center\"><img src=\"/uploads/tutientran3.jpg\" style=\"width:481px;height:310px\" /><br /></div>\r\n<div style=\"text-align:center\">Tiệm thương nhân thần bí</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nMỗi tầng đều có quy định thời gian tối đa vượt tầng. Trong thời gian quy\r\nđịnh phải phá được trận ở mỗi tầng thì mới có thể qua tầng tiếp theo</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nMỗi tầng đều có hướng dẫn của hệ thống cho người khiêu chiến phó bản. Làm\r\ntheo hướng dẫn phá trận sẽ có thể phá trận dễ dàng hơn.</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nTrận phá được càng nhiều thì phần thưởng kinh nghiệm tích lũy được càng\r\nnhiều, và có thông báo rõ ràng cho người chơi.</div>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nNgoài ra có những tầng khi người chơi tham gia vượt tầng sẽ có cơ hội nhận\r\nđược thêm các phần thưởng phong phú khác bên cạnh phần thưởng kinh nghiệm tích\r\nluỹ cơ bản.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran4.jpg\" style=\"width:268px;height:356px\" /><br /></div>\r\n<div style=\"text-align:center\">Phần thưởng may mắn Tru Tiên Trận tầng\r\n10</div>\r\n<div>Nếu quá thời gian quy định mà vẫn chưa phá được trận thì sẽ thất bại, bị\r\nđưa ra ngoài, và phó bản sẽ kết thúc.</div>\r\n<div>Đối với một số tầng đặc biệt, khi người chơi khiêu chiến phó bản phá trận\r\nthành công tầng đó trong thời gian ngắn nhất sẽ trở thành tiên chủ của tầng, và\r\nđược dựng tượng&nbsp;của bản thân tại tầng đó cũng như thông báo rộng rãi cho\r\ncác người chơi, tạo nên danh tiếng cho tiên chủ.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran5.jpg\" style=\"width:236px;height:281px\" /><br /></div>\r\n<div style=\"text-align:center\">Tiên chủ của một tầng trong phó bản</div>\r\n<div><strong>Phần thưởng</strong></div>\r\n<div>Bên cạnh phần thưởng kinh nghiệm tích luỹ và phần thưởng vượt ải may mắn,\r\nkhi vượt thành công một tầng, người chơi sẽ có cơ hội nhận được bảo rương Tru\r\nTiên trận, mở ra sẽ nhận được các loại vật phẩm phong phú.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran6.jpg\" style=\"width:244px;height:208px\" /><br /></div>\r\n<div style=\"text-align:center\">Bảo rương Tru Tiên Trận</div>\r\n<div>Tru Tiên Trận là phó bản 3x duy nhất mà người chơi có khả năng nhận được\r\ncác loại trang bị tấn công tím cao cấp lv 40, và các mảnh vỡ nguyên liệu, là\r\nvật phẩm không thể thiếu để đổi các loại trang bị tím cao cấp khác tại NPC Trao\r\nĐổi Tiên Sư.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran7.jpg\" style=\"width:218px;height:180px\" /><br /></div>\r\n<div style=\"text-align:center\">Các phần thưởng</div>\r\n<div>Sau khi vượt ải thành công, người chơi sẽ được hệ thống tự động đánh giá\r\nchiến tích và nhận được một lần quay bàn cổ phó bản, nhận được thêm 1 loại vật\r\nphẩm.</div>\r\n<div style=\"text-align:center\"><img src=\"/uploads/tutientran9.jpg\" style=\"width:500px;height:218px\" /><br /></div><div style=\"text-align:center\">Đánh giá chiến tích</div>\r\n<div>Tuỳ theo loại phó bản nào, mà sau khi hoàn thành phó bản xong bàn cổ của\r\nphó bản sẽ có phần thưởng tặng thêm tương ứng. Nếu sau khoảng thời gian 1 phút,\r\nngười chơi không mở bàn cổ phó bản, bàn cổ tự động mất.</div>\r\n<div style=\"text-align:center\">&nbsp;<img src=\"/uploads/tutientran10.jpg\" style=\"width:328px;height:340px\" /></div>\r\n<div style=\"text-align:center\">Tru Tiên bàn cổ&nbsp;</div>  ', '', '', '', null, null, null, null, null, '50324', '2013-12-02 14:04:51', '50324', '2013-12-02 14:04:55', '0', '1');
INSERT INTO `letyii_article` VALUES ('11', 'Canh giữ linh nguyên', '', '', '<div><p style=\"\" margin-bottom:7.5pt;text-align:justify;background:#fbf4e6\"\"=\"\">Trong lúc linh khí đang thiếu thốn thì nguồn linh khí chính là vật thu hút\r\nvô số người, đặc biệt là bọn yêu ma dị thú vốn không thể tu luyện kim đan, bọn\r\nchúng kết bè phái lũ lượt xông vào tấn công linh nguyên, các bậc tu tiên đã\r\ntriệu hồi các dũng sĩ phàm nhân tham gia ngăn chặn yêu ma, bảo vệ nguồn linh\r\nkhí.</p>\r\n<p style=\"\" margin-bottom:3.75pt;line-height:19.5pt;background:#fbf4e6\"\"=\"\"><strong>Điều kiện</strong></p>\r\n<p style=\"\" margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:31.5pt;text-align:justify;text-indent:-18.0pt;line-height:15.0pt;background:#fbf4e6\"\"=\"\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n<strong>Điều kiện tham gia:&nbsp;</strong><strong>Người chơi có đẳng cấp\r\ntừ 38 trở lên</strong></p>\r\n<div>·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nMỗi ngày có thể tham gia dạng cá nhân hoặc tinh anh, mỗi loại người chơi có\r\nthể tham gia 1 lần trong ngày.</div>\r\n<p style=\"\" margin-bottom:3.75pt;line-height:19.5pt;background:#fbf4e6\"\"=\"\"><strong>Cách tham gia</strong></p>\r\n<p style=\"\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Đến&nbsp;<strong>Thái Cơ Thành</strong>&nbsp;tìm&nbsp;<strong>Linh Nguyên Sư (119,150)</strong>&nbsp;đối\r\nthoại để có thể vào phó bản theo dạng đơn hoặc&nbsp;<strong>Linh Nguyên Tôn\r\n(100.150)</strong>&nbsp;để vào phó bản theo dạng tinh anh (tổ đội).</p><p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/canhgiu1.jpg\" style=\"width:158px;height:166px\" /><br /></p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\">NPC Linh Nguyên Sư</p><p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/canhgiu2.jpg\" style=\"width:188px;height:217px\" /><br /></p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\">&nbsp;NPC Linh Nguyên Tôn</p>\r\n<p style=\"\" margin-bottom:3.75pt;line-height:19.5pt;background:#fbf4e6\"\"=\"\"><strong>Khiêu chiến\r\nphó bản</strong></p>\r\n<p style=\"\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Khi vào phó bản sẽ nhìn thấy thanh vật phẩm xuất hiện phía trên thanh kỹ\r\nnăng, khi đánh bại quái trong phó bản sẽ có xác suất nhặt được vật phẩm chuyên\r\ndụng và vật phẩm sẽ chuyển đế khung vật phẩm, vật phẩm chỉ có thể dùng trong\r\nphó bản, ra ngoài sẽ mất.Chỉ có thể giữ tối đa cùng lúc 5 vật phẩm chuyên dụng, người chơi cần sử\r\ndụng các vật phẩm hiện có để có thể tiếp tục nhặt thêm các kỹ năng khác.</p><p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/anh.png\" style=\"width:622px;height:446px\" /><br /></p><p style=\"\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Người chơi cần bảo vệ&nbsp;<strong>Linh Đài - Linh Vương Hộ Pháp</strong>&nbsp;trước\r\nnhững đợt tấn công liên tục với độ khó tăng dần lên của<strong>&nbsp;Ma Tu Tà đạo</strong>.</p>\r\n<p style=\"\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Tuỳ theo khả năng của mình, người chơi cùng các Linh nguyên chiến sĩ cùng\r\nnhau bảo vệ Linh Đài để thu thập điểm kinh nghiệm tích luỹ tăng dần theo từng\r\nđợt quái xuất hiện từ Yêu Ma Quật trong 60 lượt quái để hoàn thành phó bản.</p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/canhgiu9.jpg\" style=\"width:492px;height:295px\" /><br /></p>\r\n<p style=\"text-align: center;\" text-align:center;line-height:15.0pt;background:#fbf4e6\";text-align:center\"=\"\">Bản đồ phó bản Canh giữ Linh Nguyên</p>  <p style=\"\" margin-bottom:3.75pt;line-height:19.5pt;background:#fbf4e6\"\"=\"\"><strong>Phần thưởng</strong></p>\r\n<p style=\"\" margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:31.5pt;text-align:justify;text-indent:-18.0pt;line-height:15.0pt;background:#fbf4e6\"\"=\"\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nTiêu diệt yêu ma sẽ nhận rất nhiều kinh nghiệm.</p>\r\n<p style=\"\" margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:31.5pt;text-align:justify;text-indent:-18.0pt;line-height:15.0pt;background:#fbf4e6\"\"=\"\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nTrong phó bản sẽ có thể đánh rớt trang bị xanh lam cao cấp 40 và 50, cùng\r\ncác vật phẩm quý.</p>\r\n<p style=\"\" margin-top:0cm;margin-right:0cm;margin-bottom:7.5pt;margin-left:31.5pt;text-align:justify;text-indent:-18.0pt;line-height:15.0pt;background:#fbf4e6\"\"=\"\">·&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\nTheo từng đợt quái sẽ có các loại Boss xuất hiện, đánh bại được Boss này\r\nngười chơi sẽ có cơ hội nhận được các loại vật phẩm quý, các mảnh nguyên\r\nliệu&nbsp;<strong>Tinh Hoa Nguyệt (sơ)</strong>&nbsp;và lượng điểm kinh nghiệm lớn.</p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/canhgiu10.jpg\" style=\"width:155px;height:130px\" /><br /></p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Phần thưởng có thể nhận</p>\r\n<p style=\"\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Sau khi vượt ải thành công, người chơi sẽ được hệ thống tự động đánh giá\r\nchiến tích và nhận được một lần quay bàn cổ phó bản, nhận được thêm 1 loại vật\r\nphẩm.</p><p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/canhgiu12.jpg\" style=\"width:572px;height:230px\" /><br /></p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\">&nbsp;Đánh giá chiến tích</p>\r\n<p style=\"\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\">Tuỳ theo loại phó bản nào, mà sau khi hoàn thành phó bản xong bàn cổ của\r\nphó bản sẽ có phần thưởng tặng thêm tương ứng. Nếu sau khoảng thời gian 1 phút,\r\nngười chơi không mở bàn cổ phó bản, bàn cổ tự động mất.</p><p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:justify;line-height:15.0pt;background:#fbf4e6\"\"=\"\"><img src=\"/uploads/canhgiu13.jpg\" style=\"width:335px;height:325px\" /><br /></p>\r\n<p style=\"text-align: center;\" margin-bottom:7.5pt;text-align:center;line-height:15.0pt;background:#fbf4e6\"\"=\"\">&nbsp;Canh giữ Linh Nguyên bàn cổ</p>  <br /></div>  ', '', '', '', null, null, null, null, null, '50324', '2013-12-02 16:40:10', '50324', '2013-12-02 16:40:16', '0', '1');
INSERT INTO `letyii_article` VALUES ('12', 'Đoạt bảo kỳ binh', '', '', '<div>\r\n      <h4><strong>Cách  đột nhập đào kho báu</strong></h4>\r\n      <div>\r\n        <p>Con  đường tìm kho báu rất nguy hiểm, chỉ có người chơi cấp 40 trở lên mới có thể  đến Thái Cơ Thành để nhận nhiệm vụ này tại NPC Triệu Phàm ( Thái Cơ Thành  190,60). Mỗi ngày có thể nhận nhiệm vụ này 3 lần.</p>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/doatbaokybinh/doatbao1.jpg\" height=\"261\" width=\"242\" /></p>\r\n        <p>Sau khi nhận nhiệm vụ sẽ nhận được  Bản đồ kho báu, trong bản đồ ở quốc gia khác có chỉ vị trí kho báu.</p>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/doatbaokybinh/doatbao2.jpg\" height=\"288\" width=\"444\" /></p>\r\n        <p>Người chơi có thể sử dụng chức năng  La bàn định vị để định \r\nvị chính xác toạ độ của kho báu. Sau đó có thể di chuyển  đến vị trí này\r\n ở quốc gia khác để tìm kho báu</p>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/doatbaokybinh/doatbao3.jpg\" height=\"354\" width=\"486\" /></p>\r\n        <p>Hoặc có thể sử dụng chức năng Phạm  vi truyền tống\r\n để được truyền tống đến gần nơi có kho báu. Chức năng này giúp  người \r\nchơi tiện lợi hơn trong việc thực hiện nhiệm vụ, khi không phải tốn thời\r\n  gian di chuyển đến bản đồ kho báu. Để sử dụng chức năng này, người \r\nchơi sẽ tốn  một khoảng phí 2.000 đồng (Vip miễn phí).</p>\r\n        <p>Khi đến gần vị trí kho báu sẽ có mũi  tên chỉ hướng vị trí \r\nkho báu, dựa theo hướng dẫn đó để đến chỗ kho báu, khi  thấy xuất hiện \r\nnhắc nhở “Kho báu ở ngay dưới chân” thì có thể nhấn vào Xẻng để  đào kho báu nhận Bảo Hộp Thần Bí!</p>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/doatbaokybinh/doatbao4.jpg\" height=\"227\" width=\"186\" /></p>\r\n        <p>Hộp Thần  bí (khoá) là vật phẩm\r\n mà người chơi sẽ nhận khi đào kho báu thành công. Người  chơi cần về \r\nquốc gia của mình hoặc các thành trung lập mới có thể mở hộp thần  bí, \r\nnhận được các vật phẩm giá trị.</p>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/doatbaokybinh/doatbao5.jpg\" height=\"217\" width=\"268\" /></p>\r\n      </div>\r\n      <h4> Huyết chiến đoạt bảo</h4>\r\n      <div>\r\n        <p>Khi người chơi xâm nhập vào lãnh địa của phe đối  địch để tìm\r\n kho báu, sẽ có thể gặp phải những kẻ địch chặn đường, cần đánh bại  các\r\n kẻ địch đó và trở về.</p>\r\n        <p>Hoàn thành nhiệm vụ trở về gặp NPC Triệu Phàm sẽ\r\n  nhận được phần thưởng kinh nghiệm phong phú và mở được bảo hộp đào \r\nđược, có khả  năng nhận được tiên khí, nếu may mắn còn có thể nhận được \r\nsách kỹ năng cao cấp.</p>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/doatbaokybinh/doatbao6.jpg\" height=\"249\" width=\"500\" /></p>\r\n      </div>\r\n    </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-12 14:57:03', '50336', '2013-12-12 14:58:22', '0', '1');
INSERT INTO `letyii_article` VALUES ('13', 'Hệ thống Boss', '', '', '<div>\r\n              <div>\r\n                <p>Thời thượng cổ, tiên  phàm đại loạn, linh thú quấy phá Tu Chân giới. Tu Tiên Giả tề tựu dẹp loạn linh  thú, lấy lại yên bình cho tiên cảnh.</p>\r\n                </div>\r\n        <h4>Điều kiện</h4>\r\n                <div>\r\n                  <ul><li>\r\n                      <p>Để có thể  săn các loại Boss trong game, người chơi cần phải đạt được đẳng cấp từ 30 trở  lên.</p>\r\n                    </li></ul>\r\n                </div>\r\n                <h4> Các loại Boss</h4>\r\n                <div>\r\n                  <p>Hiện nay có  tổng cộng 7 loại Boss mà người chơi có\r\n thể săn trong game. Sau khi đánh bại các  loại Boss, người chơi sẽ có \r\ncơ hội nhận được các phần thưởng đa dạng. </p>\r\n                  <p>Để kiểm tra  các loại Boss mà mình có thể săn, \r\nngười chơi có thể vào giao diện Boss  trong&nbsp;mục ngày ở thanh chức năng \r\nbên dưới  (phím tắt “O”). Hệ thống sẽ cung cấp các thông tin cơ bản về các loại Boss ngươi  chơi có thể săn.<br />\r\n                  </p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss1.jpg\" height=\"353\" width=\"500\" /></p>\r\n                  <p><strong>Boss Viên Thần (U Minh Động):</strong></p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss2.jpg\" height=\"216\" width=\"256\" /></p>\r\n                  <p style=\"text-align:center\">Boss Viên Thần</p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss3.jpg\" height=\"182\" width=\"224\" /></p>\r\n                  <p style=\"text-align:center\">Vật phẩm mang theo</p>\r\n                  <p><strong>Boss Âm Phong U Linh ( Âm Phong Động):</strong></p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss4.jpg\" height=\"238\" width=\"246\" /></p>\r\n                  <p style=\"text-align:center\">Boss Âm Phong U Linh</p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss5.jpg\" height=\"183\" width=\"216\" /></p>\r\n                  <p style=\"text-align:center\">Vật phẩm mang theo</p>\r\n                  <p><strong>Boss Hắc Phong Hùng  (Hắc Phong Động):</strong></p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss6.jpg\" height=\"186\" width=\"192\" /></p>\r\n                  <p style=\"text-align:center\">Boss Hắc Phong Hùng</p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss7.jpg\" height=\"182\" width=\"225\" /></p>\r\n                  <p style=\"text-align:center\">Vật phẩm mang theo</p>\r\n                  <p><strong>Boss Hoả Văn Thú (có  ở 3 bản đồ Linh Thú Sơn, Đơn Nhai Sơn, Kỳ Bàn Cốc):</strong></p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss10.jpg\" height=\"322\" width=\"342\" /></p>\r\n                  <p style=\"text-align:center\">Boss Hỏa Văn Thú</p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss9.jpg\" height=\"181\" width=\"221\" /></p>\r\n                  <p style=\"text-align:center\">Vật phẩm mang theo</p>\r\n                  <p><strong>Boss Băng Giáp Thú (Long Tuyết Vực):</strong></p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss14.jpg\" height=\"291\" width=\"306\" /></p>\r\n                  <p style=\"text-align:center\">Boss Âm Phong U Linh</p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/tieudietboss/boss15.jpg\" height=\"178\" width=\"223\" /></p>\r\n                  <p style=\"text-align:center\">Vật phẩm mang theo</p></div></div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 10:19:24', '50336', '2013-12-13 10:19:24', '0', '1');
INSERT INTO `letyii_article` VALUES ('14', 'Bảo tiêu Tiên Minh', '', '', 'Thông  qua nhiệm vụ này, người chơi có thể mua thương phẩm ở vùng này và đem bán ở  vùng khác với giá cao\r\n hơn, từ đó kiếm thêm tiền cho tiên minh. Tiền tiên minh  có thể dùng để\r\n xây các công trình trong tiên minh và mở các hoạt động trong  tiên \r\nminh.\r\n        <h4>Điều kiện tham gia</h4>\r\n        <div>\r\n          <ul><li>Người chơi có đẳng cấp 30 trở lên.</li><li>Đã gia nhập Tiên Minh.</li></ul>\r\n        </div>\r\n        <h4> Cách tham gia</h4>\r\n        <div>\r\n          <p>Đến gặp NPC Tiên  Thương Vân ( Thái Cơ Thành 198,50) để nhận ngân phiếu đổi hàng, mỗi người mỗi  ngày có thể nhận nhiệm vụ ngân phiếu 3 lần.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/baotieutienminh/baothuong1.jpg\" height=\"250\" width=\"241\" /></p>\r\n          <p>Nhấn vào ngân phiếu sẽ mở ra bảng  danh sách \r\ncác thương nhân, có thể nhấn vào để tự động tìm đường, dùng ngân phiếu  \r\nđể mua hàng hóa với giá rẻ và đến nơi khác bán lại với giá cao hơn. Khi \r\nsố ngân  phiếu kiếm được đạt mục tiêu quy định thì có thể hoàn \r\nthành nhiệm vụ, nhận được  phần thưởng. </p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/baotieutienminh/baothuong2.jpg\" height=\"252\" width=\"313\" /></p>\r\n          <p><strong>Quy tắc bảo tiêu: </strong></p>\r\n          <ul><li>Các  món hàng bán ở chỗ thương nhân cứ mỗi 5 phút sẽ làm\r\n mới 1 lần, người chơi có thể  đến chỗ các thương nhân này để mua hoặc \r\nbán các món hàng kiếm lời.</li></ul>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/baotieutienminh/baothuong3.jpg\" height=\"259\" width=\"500\" /></p>\r\n          <ul><li>Ngoài ra, thương nhân  bào thương ở các địa điểm còn có \r\nthể bán các đặc sản riêng biệt. Lợi nhuận của  những đặc sản này rất \r\ncao, người chơi có thể mua để thu được lợi nhuận nhiều  hơn.</li></ul>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/baotieutienminh/baothuong4.jpg\" height=\"260\" width=\"500\" /></p>\r\n          <ul><li>\r\n              <p>Tổng  số hàng tối đa mà người chơi có thể vận chuyển 1 lần là 100 món.</p>\r\n            </li><li>\r\n              <p>Trong lúc vận chuyển hàng vẫn có thể vào phó bản và làm nhiệm vụ hộ tống.</p>\r\n            </li></ul>\r\n          <p><strong>Quy tắc cướp hàng:</strong> </p>\r\n          <ul><li>\r\n              <p>Nếu đang trên đường vận chuyển mà bị  tử vong (ngoại \r\ntrừ bị tử vong trong phó bản) thì số ngân phiếu sẽ mất, không thể  hoàn \r\nthành được nhiệm vụ.</p>\r\n            </li><li>\r\n              <p>Nếu bị người chơi ở quốc gia khác đánh bại thì người  chơi đó sẽ có xác suất cướp được một lượng ngân phiếu. </p>\r\n            </li></ul>\r\n          <br />\r\n        </div>\r\n        <h4> Phần thưởng</h4>\r\n        <div>\r\n          <p>Đạt  được số ngân phiếu mà nhiệm vụ yêu cầu thì sẽ nhận \r\nđược phần thưởng. Nếu số  ngân phiếu chưa đạt mà trả nhiệm vụ thì xem \r\nnhư là hủy nhiệm vụ.</p>\r\n          <ul><li>\r\n              <p>Phần  thưởng tiên minh: Tiên minh nhận thêm tài nguyên, điểm phồn vinh. Cấp độ tiên  minh càng cao thì phần thưởng nhận được càng nhiều.</p>\r\n            </li><li>\r\n              <p>Phần thưởng cá nhân: Nhận\r\n được điểm cống hiến và kinh nghiệm. Cấp độ tiên  minh càng cao thì nhân\r\n vật nhận càng nhiều điểm cống hiến, cấp độ nhân vật càng  cao thì càng \r\nnhận được nhiều điểm kinh nghiệm.</p>\r\n            </li></ul>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/baotieutienminh/baothuong5.jpg\" height=\"357\" width=\"500\" /></p>\r\n        </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 10:27:15', '50336', '2013-12-13 10:32:46', '0', '1');
INSERT INTO `letyii_article` VALUES ('15', 'Nướng cá tắm suối', '', '', 'Là một game\r\n nhỏ trong giao diện bản  đồ Tiên minh, người chơi sau khi đạt đến đẳng \r\ncấp 35 trở lên sẽ nhận đươc kỹ  năng này trong bảng kỹ năng cơ bản.\r\n        <h4>Điều kiện tham gia</h4>\r\n        <div>\r\n          <ul><li>Người chơi đã gia nhập Tiên Minh.</li></ul>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh11.jpg\" height=\"344\" width=\"378\" /></p>\r\n        </div>\r\n        <h4> Cách tham gia</h4>\r\n        <div style=\"text-align:left\">\r\n          <p style=\"text-align:left\">Người chơi đến hồ nước trong bản đồ  Tiên minh để sử dụng \r\nkỹ năng câu cá. Cần có mồi câu để có thể câu cá, mồi câu  được bán ngay \r\ntrong giao diện câu cá.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh12.jpg\" height=\"278\" width=\"500\" /></p>\r\n          <p>Người chơi nhấp chuột trái dể tiến  hành câu cá. Nếu gặp \r\nphải vỏ sò sẽ bị mất 1 mồi câu nhưng không nhận được cá.  Mỗi ngày có \r\nthể mua tối đa 30 mồi câu. Sử dụng mồi câu càng cao càng nhận được  cá và vật phẩm cấp càng cao.</p>\r\n          <p>Sau khi câu hết mồi câu, người chơi  có thể nhận được các \r\nloại cá phẩm chất khác nhau, trân châu các loại và bảo  rương cũ nát. \r\nNgười chơi đến Tiên minh hoả của mình để bắt đầu nướng cá.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh13.jpg\" height=\"265\" width=\"500\" /></p>\r\n          <p style=\"text-align:left\">Sau khi tiến  hành nướng cá, hệ thống xuất hiện thanh chức \r\nnăng nhặt cá. Người chơi cần phải  nhăt cá trong giai đoạn tỷ lệ cá \r\nchính càng cao càng tốt. Nếu tỷ lệ cá chin  càng thấp, sẽ nhận được cá \r\nkhét, không thể sử dụng.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh14.jpg\" height=\"112\" width=\"500\" /></p>\r\n          <p>Sau khi nuớng  cá thành công, có thể sử dụng cá và trân \r\nchâu. Cấp và phẩm chất cá và trân châu  càng cao, hiệu quả nhận được \r\ncàng lớn.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh15.jpg\" height=\"210\" width=\"246\" /><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh16.jpg\" height=\"174\" width=\"246\" /></p>\r\n          <p>Cá nướng thất  bại không thể sử dụng, có thể bán cho hệ thống hoặc huỷ.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/nuongcatamsuoi/tienminh17.jpg\" height=\"210\" width=\"242\" /></p>\r\n        </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 11:16:11', '50336', '2013-12-13 11:16:11', '0', '1');
INSERT INTO `letyii_article` VALUES ('16', 'Hệ thống Linh thú', '', '', 'Thú cưng là bạn đồng hành của người  chơi, có khả năng hỗ trợ cho người chơi đáng kể trên con đường tu luyện. Chặng đường du hiệp của các đạo hữu không thể thiếu được những chú thú cưng đáng yêu này!\r\n        <h4>Giới thiệu</h4>\r\n        <div>\r\n          <p>Khi người chơi hoàn thành nhiệm vụ  thử thách nhâp môn ở cấp 7, người chơi sẽ được hệ thống tặng 1 thú cưng Long  Bảo Bảo, tư chất 450 (tư chất thú cưng càng cao, phẩm chất thú cưng càng cao).</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung1.jpg\" height=\"322\" width=\"500\" /></p>\r\n          <p>Để mở giao diện thú cưng, người chơi  có thể nhấn phím tắt W hoặc chọn giao diện Linh Sủng trong thanh công cụ  chức năng phía dưới.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung2.jpg\" height=\"125\" width=\"489\" /></p>\r\n        </div>\r\n        <h4>Tẩy tủy thú cưng</h4>\r\n        <div>\r\n          <p>Có thể chia thú cưng ra làm 3 loại  dựa theo tính chất thuộc tính tấn công của thú cưng:</p>\r\n          <ul><li>Hệ vật lý.</li><li>Hệ pháp công.</li><li>Hệ thể chất (đánh vật công, có ưu thế về sinh lực, vật phòng, pháp phòng).</li></ul>\r\n          <p>Người chơi có thể tẩy tuỷ thú cưng của mình bất cứ lúc nào \r\nđể lựa chọn hệ sao  cho phù hợp với bản thân và mục đích sử dụng. Để sử \r\ndụng tính năng tẩy tuỳ,  người chơi cần có vật phẩm Linh Sủng Tẩy Tuỷ Đơn ( có bán trong cửa hàng giá 10  vàng/viên). Mỗi lần tẩy tuỷ cần phải có 1 Linh Sủng Tẩy Tuỷ Đơn.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung3.jpg\" height=\"149\" width=\"147\" /></p>\r\n          <p>Trong giao diện thú cưng, người chơi  có thể bấm vào phím Vật, Pháp hoặc Thể để kiểm tra hệ của thú cưng và  tiến hành tẩy tuỷ thú cưng của mình.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung4.jpg\" height=\"323\" width=\"497\" /></p>\r\n          <p>Người chơi có thể để cho thú cưng  của mình thay đổi giữa 3\r\n loại hệ tuỳ ý. Sau khi tẩy tuỷ thành công, thuộc tính  cơ bản và thuộc \r\ntính chiến đấu của thú cưng sẽ được hệ thống tự động tính lại,  và sẽ \r\nđược tăng sao cho phù hợp với hệ phái mà thú cưng chuyển qua.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung5.jpg\" height=\"365\" width=\"485\" /></p>\r\n          <p>Lưu ý: khi thú cưng đang trong trạng  thái\r\n xuất chiến hoặc đang tu luyện, người chơi sẽ không thể tiến hành tẩy \r\ntuỷ  cho thú cưng này. </p>\r\n        </div>\r\n        <h4>Tăng linh tính</h4>\r\n        <div>\r\n          <p>Là tính năng tăng các thuộc tính chiến đấu và  phòng ngự \r\ncủa thú cưng. Khi tăng cấp linh tính, các điểm thuộc tính chiến đấu  và \r\nphòng ngự của thú cưng sẽ tăng lên.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung6.jpg\" height=\"322\" width=\"500\" /></p>\r\n          <ul><li>Sủng vật ban đầu có linh tính cấp 0, có thể tiến  hành tăng cấp linh tính. </li><li>Linh tính tổng cộng có 20 cấp, mỗi cấp tùy vào tỉ lệ \r\nphần trăm sẽ tăng các thuộc tính chiến đấu cho sủng  vật, tăng thành \r\ncông linh tính sẽ tự động tăng đầy điểm thuộc tính.</li><li>Mỗi  lần tăng linh tính, dù thành công hay thất bại thì \r\nđều sẽ tăng một lượng điểm  chúc phúc linh tính nhất định, khi cấp độ \r\nlinh tính thấp hơn cấp độ điểm chúc  phúc thì có thể dùng 20% lượng điểm\r\n chúc phúc để hồi phục lại cấp độ bằng với cấp  độ điểm chúc phúc. </li><li>Sau khi cấp chúc phúc linh tính đạt đến mốc yêu cầu, và \r\ncấp chúc phúc linh tính phải cao hơn cấp linh hồn, người  chơi có thể \r\ntiến hành nhấp vào nút Nhận để tiến hành nhận chúc phúc.</li></ul>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung7.jpg\" height=\"198\" width=\"500\" /></p>\r\n          <ul><li>Sau khi nhận chúc phúc, thú cưng của người chơi  sẽ đạt \r\nlinh tính và cấp linh hồn mới, thuộc tính chiến đấu và phòng ngự được  \r\ntăng lên.</li><li>Để tăng điểm chúc phúc, người chơi cần phải có  vật phẩm\r\n Linh Sủng Đơn ( có bán trong cửa hàng hoặc tham gia các tính năng khác \r\n trong game) mới có thể tiến hành tăng điểm chúc phúc linh tính và điểm linh hồn.</li></ul>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang/hethonglinhthu/thucung8.jpg\" height=\"154\" width=\"151\" /></p>\r\n          <div>\r\n            <p>Ghi chú</p>\r\n            <div>\r\n              <ul><li>\r\n                  <p>Khi tăng điểm linh hồn, dù thành công hay thất  bại thì điểm chúc phúc linh tính vẫn sẽ tăng.</p>\r\n                </li><li>\r\n                  <p>Sau khi nhận chúc phúc, thú cưng đạt  cấp linh tính và linh hồn tương ứng, nhưng sẽ bị mất đi 20% điểm chúc phúc hiện  có.</p>\r\n                </li></ul>\r\n            </div>\r\n          </div>\r\n        </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 14:14:38', '50336', '2013-12-13 14:14:38', '0', '1');
INSERT INTO `letyii_article` VALUES ('17', 'Hệ thống giao dịch', '', '', '<div>\r\n                <h4>Hệ thống giao dịch</h4>\r\n                <div>\r\n                  <ul><li>Bước 1: Nhấn vào tên  người chơi, chọn phần giao dịch là có thể gửi yêu cầu giao dịch.</li><li>Bước 2: Sau khi đối phương đồng ý, màn hình của  cả 2 bên sẽ hiện ra giao diện giao dịch.</li><li>Bước 3: Nhấn chuột trái vào vật phẩm hoặc kéo rê  vào khung giao dịch, sau đó điền số lượng tiền tương ứng vào.</li><li>Bước 4: Sau khi 2 bên  khóa giao dịch, xác nhận giao dịch thì trạng thái giao dịch hoàn thành.</li></ul>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/hethonggiaodich/giao-dich.jpg\" height=\"347\" width=\"500\" /></p>\r\n                  <p>Giao dịch<strong><br />\r\n                  </strong></p>\r\n                </div>\r\n                <h4>Hệ thống bày bán</h4>\r\n                <div>\r\n                  <ul><li>\r\n                      <p>Bước 1: Mở túi và chọn nút bày bán.</p>\r\n                    </li><li>\r\n                      <p>Bước 2: Trong giao diện hiện ra, nhấn chọn vật  phẩm hoặc kéo rê vào ô bày bán, sau đó chọn giá bán.</p>\r\n                    </li><li>\r\n                      <p>Bước 3: Điền lời rao bán.</p>\r\n                    </li></ul>\r\n                </div>\r\n              </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 14:20:41', '50336', '2013-12-13 14:20:41', '0', '1');
INSERT INTO `letyii_article` VALUES ('18', 'Thú cưng', '', '', 'Thú cưng là bạn đồng hành của người  \r\nchơi, có khả năng hỗ trợ cho người chơi đáng kể trên con đường tu luyện.\r\n Chặng đường du hiệp của các đạo hữu không thể thiếu được những chú thú \r\ncưng đáng yêu này!<br /><h4>Giới thiệu</h4>\r\n               <div>\r\n                 <p>Khi người chơi hoàn thành nhiệm vụ  thử thách nhâp môn ở cấp 7, người chơi sẽ được hệ thống tặng 1 thú cưng Long  Bảo Bảo, tư chất 450 (tư chất thú cưng càng cao, phẩm chất thú cưng càng cao).</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung1.jpg\" height=\"322\" width=\"500\" /></p>\r\n<p>Để mở giao diện thú cưng, người chơi  có thể nhấn phím tắt W hoặc chọn giao diện Linh Sủng trong thanh công cụ  chức năng phía dưới.</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung2.jpg\" height=\"125\" width=\"489\" /></p>\r\n              </div>\r\n              <a></a>\r\n               <h4>Tẩy tủy thú cưng</h4>\r\n               <div>\r\n                 <p>Có thể chia thú cưng ra làm 3 loại  dựa theo tính chất thuộc tính tấn công của thú cưng:</p>\r\n                 <ul><li>Hệ vật lý.</li><li>Hệ pháp công.</li><li>Hệ thể chất (đánh vật công, có ưu thế về sinh lực, vật phòng, pháp phòng).</li></ul>\r\n                 <p>Người chơi có thể tẩy tuỷ thú cưng của mình bất cứ \r\nlúc nào để lựa chọn hệ sao  cho phù hợp với bản thân và mục đích sử \r\ndụng. Để sử dụng tính năng tẩy tuỳ,  người chơi cần có vật phẩm Linh Sủng Tẩy Tuỷ Đơn ( có bán trong cửa hàng giá 10  vàng/viên). Mỗi lần tẩy tuỷ cần phải có 1 Linh Sủng Tẩy Tuỷ Đơn.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung3.jpg\" height=\"149\" width=\"147\" /></p>\r\n<p>Trong giao diện thú cưng, người chơi  có thể bấm vào phím Vật, Pháp hoặc Thể để kiểm tra hệ của thú cưng và  tiến hành tẩy tuỷ thú cưng của mình.</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung4.jpg\" height=\"323\" width=\"497\" /></p>\r\n<p>Người chơi có thể để cho thú cưng  của mình thay đổi giữa 3 loại hệ \r\ntuỳ ý. Sau khi tẩy tuỷ thành công, thuộc tính  cơ bản và thuộc tính \r\nchiến đấu của thú cưng sẽ được hệ thống tự động tính lại,  và sẽ được \r\ntăng sao cho phù hợp với hệ phái mà thú cưng chuyển qua.</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung5.jpg\" height=\"365\" width=\"485\" /></p>\r\n<p><strong>Lưu ý:</strong> khi thú cưng đang trong trạng  thái xuất \r\nchiến hoặc đang tu luyện, người chơi sẽ không thể tiến hành tẩy tuỷ  cho\r\n thú cưng này.              </p>\r\n              </div>\r\n                <a></a>\r\n               <h4>Tăng linh tính</h4>\r\n               <div>\r\n                 <p>Là tính năng tăng các thuộc tính chiến đấu và  phòng\r\n ngự của thú cưng. Khi tăng cấp linh tính, các điểm thuộc tính chiến đấu\r\n  và phòng ngự của thú cưng sẽ tăng lên.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung6.jpg\" height=\"322\" width=\"500\" /></p>\r\n                 <ul><li>Sủng vật ban đầu có linh tính cấp 0, có thể tiến  hành tăng cấp linh tính.\r\n                   </li><li>Linh tính tổng cộng có 20 cấp, mỗi cấp tùy vào tỉ lệ \r\nphần trăm sẽ tăng các thuộc tính chiến đấu cho sủng  vật, tăng thành \r\ncông linh tính sẽ tự động tăng đầy điểm thuộc tính.</li><li>Mỗi  lần tăng linh tính, dù thành công hay thất bại thì \r\nđều sẽ tăng một lượng điểm  chúc phúc linh tính nhất định, khi cấp độ \r\nlinh tính thấp hơn cấp độ điểm chúc  phúc thì có thể dùng 20% lượng điểm\r\n chúc phúc để hồi phục lại cấp độ bằng với cấp  độ điểm chúc phúc. </li><li>Sau khi cấp chúc phúc linh tính đạt đến mốc yêu cầu, và \r\ncấp chúc phúc linh tính phải cao hơn cấp linh hồn, người  chơi có thể \r\ntiến hành nhấp vào nút Nhận để tiến hành nhận chúc phúc.</li></ul>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung7.jpg\" height=\"198\" width=\"500\" /></p>\r\n                 <ul><li>Sau khi nhận chúc phúc, thú cưng của người chơi  \r\nsẽ đạt linh tính và cấp linh hồn mới, thuộc tính chiến đấu và phòng ngự \r\nđược  tăng lên.</li><li>Để tăng điểm chúc phúc, người chơi cần phải có  \r\nvật phẩm Linh Sủng Đơn ( có bán trong cửa hàng hoặc tham gia các tính \r\nnăng khác  trong game) mới có thể tiến hành tăng điểm chúc phúc linh \r\ntính và điểm linh hồn.</li></ul>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung8.jpg\" height=\"154\" width=\"151\" /></p>\r\n  <div>\r\n    <p><strong>Ghi chú</strong></p>\r\n    <div>\r\n      <ul><li>\r\n          <p>Khi tăng điểm linh hồn, dù thành công hay thất  bại thì điểm chúc phúc linh tính vẫn sẽ tăng.</p>\r\n        </li><li>\r\n          <p>Sau khi nhận chúc phúc, thú cưng đạt  cấp linh tính và linh hồn tương ứng, nhưng sẽ bị mất đi 20% điểm chúc phúc hiện  có.</p>\r\n        </li></ul>\r\n    </div>\r\n  </div></div>\r\n  <a></a>\r\n               <h4>Tăng độ trưởng thành</h4>\r\n               <div>\r\n                 <p>Là tính năng tăng các thuộc tính cơ bản của thú  \r\ncưng. Khi tăng độ trưởng thành, các điểm thuộc tính cơ bản của thú cưng \r\nsẽ tăng  lên.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung9.jpg\" height=\"325\" width=\"500\" /></p>\r\n                 <ul><li>\r\n                   <p>Độ trưởng thành ban đầu là 0, tổng cộng có 40 cấp.</p>\r\n                 </li><li>\r\n                   <p>Cấp độ trưởng thành có ảnh hưởng trực tiếp đến \r\nđiểm thuộc tính cơ bản có thể  tăng khi sủng vật thăng cấp, nếu trưởng \r\nthành thành công thì điểm thuộc tính sẽ  tự động tăng đầy.</p>\r\n                 </li><li>\r\n                   <p>Điểm trưởng thành cũng giống linh tính, mỗi khi \r\ntăng thất bại  hoặc thành công đều nhận được một lượng điểm chúc phúc, \r\ncó thể dùng điểm chúc  phúc để khôi phục lại cấp độ trưởng thành bằng \r\nvới cấp độ điểm chúc phúc.</p>\r\n                 </li><li>\r\n                   <p>Sau  khi cấp chúc phúc trưởng thành đạt đến mốc \r\nyêu cầu, và cấp  chúc phúc trưởng thành phải cao hơn cấp linh hồn, người\r\n chơi có thể tiến  hành nhấp vào nút Nhận để tiến hành nhận chúc phúc.</p>\r\n                 </li></ul>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung10.jpg\" height=\"200\" width=\"500\" /></p>\r\n                 <ul><li>\r\n                   <p>Sau khi nhận chúc phúc, thú cưng của người chơi  \r\nsẽ đạt cấp trưởng thành và cấp linh hồn mới, thuộc tính cơ bản của thú \r\ncưng được  tăng lên.</p>\r\n                 </li><li>\r\n                     <p>Để tăng điểm trưởng thành, người chơi cần phải  có vật phẩm Đá Trưởng Thành\r\n ( có bán trong cửa hàng hoặc tham gia các tính năng  khác trong game) \r\nmới có thể tiến hành tăng điểm chúc phúc trưởng thành và điểm  linh hồn.</p>\r\n                   </li></ul>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung11.jpg\" height=\"249\" width=\"244\" /></p>\r\n                 <div>\r\n                   <p><strong>Ghi chú</strong></p>\r\n                   <div>\r\n                     <ul><li>\r\n                         <p>Khi tăng điểm linh hồn trưởng thành, dù thành  công hay thất bại thì điểm chúc phúc trưởng thành vẫn sẽ tăng.</p>\r\n                       </li><li>\r\n                         <p>Sau khi nhận chúc phúc, thú cưng  đạt cấp trưởng thành và linh hồn tương ứng, nhưng sẽ bị mất đi 20% điểm chúc  phúc hiện có.</p>\r\n                       </li></ul>\r\n                   </div>\r\n                 </div>\r\n            </div>\r\n              <a></a>\r\n               <h4>Kỹ năng thú cưng</h4>\r\n               <div>\r\n                 <p>Là tính năng cho phép thú cưng của người chơi  có \r\nthể học các kỹ năng khác nhau, giúp hỗ trợ cho chủ nhân một cách tốt \r\nnhất. Bảng  kỹ năng thú cưng nằm phía dưới bên phải trong giao diện thú \r\ncưng.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung12.jpg\" height=\"328\" width=\"500\" /></p>\r\n                 <p>Thú cưng cấp 1 sẽ có thể sử dụng cùng lúc tối  đa 4 \r\nkỹ năng. Khi thú cưng được huấn luyện đến một mức nhất định về cấp linh \r\n tính và cấp trưởng thành, thú cưng có thể mở thêm các ô kỹ năng. Tổng \r\ncộng thú  cưng có thể sử dụng tối đa 8 kỹ năng.</p>\r\n                 <ul><li>Khi linh tính đạt đến đẳng cấp 8, có thể mở 1 ô  kỹ năng.</li><li>Khi linh tính đạt đến đẳng cấp 12, có thể mở 1  ô kỹ năng.</li><li>Khi trưởng thành đạt đến đẳng cấp 20, có thể mở  1 ô kỹ năng.</li><li>Khi trưởng thành đạt đến đẳng cấp 28, có thể mở  1 ô kỹ năng.</li></ul>\r\n                 <p>Về phẩm chất của kỹ năng, hiện tại các kỹ năng  thú \r\ncưng được chia làm 4 loại theo thứ tự từ thấp đến cao theo các màu sắc \r\nvà  tên gọi riêng biệt gồm: lục-sơ, lam-trung, tím-cao, cam-đỉnh.</p>\r\n                 <p>Có thể chia các kỹ năng mà thú cưng có thể học  ra các loại: kỹ năng hỗ trợ chủ nhân, kỹ năng đặc tính thú cưng, kỹ năng tấn  công đơn-nhóm của thú cưng.\r\n Trong Cửa hàng có bán 6 loại sách kỹ năng cơ bản  để người chơi có thể \r\ndễ dàng mua và sử dụng cho các loại thú cưng của mình gồm:</p>\r\n                 <table cellpadding=\"0\" cellspacing=\"0\" width=\"80%\">\r\n                   <thead>\r\n                     <tr>\r\n                       <th colspan=\"2\">Kỹ năng</th>\r\n                     </tr>\r\n                   </thead>\r\n                   <tbody>\r\n                     <tr>\r\n                       <td>\r\n                         <p><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung13.jpg\" height=\"307\" width=\"256\" /></p>\r\n                         <p>Tử Liệt Sơ - Sách</p></td>\r\n                       <td width=\"362\">\r\n                         <p><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung14.jpg\" height=\"330\" width=\"257\" /></p>\r\n                         <p>Liên Kích Sơ - Sách</p></td>\r\n                     </tr>\r\n                     <tr>\r\n                       <td><p><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung15.jpg\" height=\"290\" width=\"255\" /></p><p>Man Kích Sơ- Sách</p></td>\r\n                       <td><p><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung16.jpg\" height=\"270\" width=\"255\" /></p><p>Bội Nguyên Sơ - Sách</p></td>\r\n                     </tr>\r\n                     <tr>\r\n                       <td><p><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung17.jpg\" height=\"268\" width=\"253\" /></p><p>Hoạt Lực Sơ - Sách</p></td>\r\n                       <td><p><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung18.jpg\" height=\"268\" width=\"257\" /></p><p>Hoàn Thần Sơ - Sách</p></td>\r\n                     </tr>\r\n                     </tbody>\r\n                 </table>\r\n                 <ul><li>Trong giao diện Kỹ năng,\r\n người chơi chọn các  loại sách kỹ năng hiện đang có trong túi hành \r\ntrang của mình và loại thú cưng cần  học để tiến hành học kỹ năng (phí \r\nhọc:1000 đồng).</li><li>Sau khi học thành công, kỹ  năng sẽ được tự động chuyển vào ô kỹ năng của thú cưng. </li><li>Thú cưng chỉ có thể học kỹ năng chưa được học, và phải học theo trình  tự của kỹ năng từ loại thấp đến cao.</li></ul>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung19.jpg\" height=\"321\" width=\"500\" /></p>\r\n                 <p>Người chơi có thể tìm kiếm các loại sách kỹ năng  \r\ntrung, cao, đỉnh trong quá trình tham gia các tính năng game hoặc bằng \r\ncách sử  dụng vật phẩm Đá Ức Hồn để đánh thức các kỹ năng cấp cao khác, \r\nnhằm dễ dàng hơn  trong việc luyện kỹ năng cho thú cưng. Đá Ức Hồn được bán trong Cửa hàng giá 30  vàng/viên.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung20.jpg\" height=\"307\" width=\"244\" /></p>\r\n                 <p>Người chơi có thể sử dụng trực tiếp Đá Ức Hồn\r\n hoặc qua giao diện kỹ năng của thú cưng, sẽ nhận được ngẫu nhiên một kỹ\r\n năng mới.  Nếu không muốn nhận kỹ năng này, có thể chọn Làm mới miễn phí (1ngày được 1  lần) hoặc Tạo mới kỹ năng ( 10 vàng/lần).</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung21.jpg\" height=\"301\" width=\"241\" /></p>\r\n                 <p>Ngoài ra, người chơi còn có thể chọn mục Làm mới  hàng loạt\r\n (tốn phí 10 vàng/lần) để được làm mới cung lúc 12 kỹ năng các loại.  \r\nKhi sử dụng chức năng này, người chơi sẽ có cơ hội nhận được nhiều hơn \r\ncác loại  kỹ năng khác ở cấp độ cao hơn. Khi làm mới hàng loạt nhiều \r\nlần, số sao sẽ tăng  dần, số sao càng lớn, cơ hội nhận được kỹ năng cấp \r\ncao ( kỹ năng cao, đỉnh) các  loại sẽ càng cao.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung22.jpg\" height=\"532\" width=\"500\" /></p>\r\n                 <p>Khi người chơi chọn Đánh thức kỹ năng nào sẽ  nhận được sách kỹ năng loại đó, và hệ thống tự động trừ đi 1 Đá Ức Hồn có trong  hành trang.</p>\r\n                 <p><strong>Lưu ý:</strong> Làm mới \r\nhàng loạt được 12 kỹ năng cùng lúc,  nhưng chỉ có thể chọn 1 kỹ năng để \r\nđánh thức. Sau khi đánh thức thành công, bảng  làm mới sẽ mất.          \r\n    </p>\r\n            </div>\r\n              <a></a>\r\n               <h4>Phong ấn kỹ năng</h4>\r\n               <div>\r\n                 <p>Khi người chơi có được nhiều hơn 8 loại kỹ năng tối \r\nđa  nhưng vẫn muốn tìm kiếm các kỹ năng khác phù hợp hơn, mà không muốn \r\nmất đi kỹ  năng cũ, có thể sử dụng chức năng Phong Ấn Thú Hồn để cất các kỹ năng mình muốn  vào kho.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung23.jpg\" height=\"324\" width=\"500\" /></p>\r\n                 <p>Để sử dụng chức năng này, người chơi cần phải có vật phẩm  Phong Ấn Thú Hồn (được bán trong Cửa hàng). Phong Ấn Thú Hồn\r\n có 4 loại, ứng với  4 loại kỹ năng. Muốn phong ấn kỹ năng loại nào \r\n(sơ-trung-cao-đỉnh) thì cần phải  có Phong Ấn Thú Hồn loại đó.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung26.jpg\" height=\"191\" width=\"150\" /></p>\r\n                 <p style=\"text-align:center\">Phong Ấn Thú Hồn</p>\r\n                 <p>Muốn phong ấn kỹ năng, người chơi cần lựa chọn kỹ năng mình  cần phong ấn và đưa vào kho thú hồn, gọi là phụ hồn.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung28.jpg\" height=\"223\" width=\"500\" /></p>\r\n                 <p>Sau khi đưa vào kho, người chơi có thể tuỳ ý đưa kỹ \r\nnăng  đó cho thú cưng khác, hoặc cất giữ kỹ năng đó trong kho (tối đa \r\nchứa được 20 kỹ  năng).</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung29.jpg\" height=\"321\" width=\"500\" /></p>\r\n<div>\r\n  <p><strong>Ghi chú</strong></p>\r\n  <div>\r\n    <ul><li>\r\n        <p>Mỗi kỹ năng chỉ cần tốn Phong Ấn Thú Hồn 1 lần, sau này  có thể lấy ra và cất vào kho thú hồn tuỳ ý.</p>\r\n      </li><li>\r\n        <p>Kỹ năng sau khi đã phụ hồn, muốn  thăng cấp cho kỹ năng đó, cần đưa ra mang vào cho thú cưng, và tiến hành học kỹ  năng này.</p>\r\n      </li><li>\r\n        <p>Sau khi thăng cấp kỹ năng này, muốn cất kỹ năng này vào kho cần tiến  hành phụ hồn lại cho kỹ năng đó.</p>\r\n      </li></ul>\r\n  </div>\r\n</div>\r\n            </div>\r\n              <a></a>\r\n               <h4>Biến hình thú cưng</h4>\r\n               <div>\r\n                 <p>Biến hình là tính năng dung để thay đổi ngoại hình \r\ncho  thú cưng tuỳ theo ý thích của chủ nhân. Để sử dụng tính năng này, \r\nngười chơi cần  có vật phẩm Thác Bản ( có bán trong Cửa hàng) mới có thể tiến hành hoá hình cho  thú cưng của mình.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung30.jpg\" height=\"208\" width=\"242\" /></p>\r\n<p>Người chơi chỉ có thể hoá hình thú mà mình đang hiện có,  và thuộc \r\ntính của thú cưng sau khi hoá hình thành công sẽ không thay đổi, thú  \r\nphụ sau khi hoá hình cũng không bị mất đi.</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung31.jpg\" height=\"300\" width=\"463\" /></p>\r\n<p style=\"text-align:center\">Biến hình thú cưng</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung33.jpg\" height=\"252\" width=\"426\" /></p>\r\n<p>Để phục hồi  lại hình dạng ban đầu của thú cưng, người chơi có thể đến NPC Đơn Thu (Thái Cơ  Thành 149,234) để có thể khôi phục lại hình dạng thú cưng miễn phí.</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung34.jpg\" height=\"252\" width=\"243\" /></p>\r\n<p><strong>Lưu ý:</strong> Thú cưng trong giai đoạn xuất chiến  không thể khôi phục biến hình.              </p>\r\n            </div>\r\n              <a></a>\r\n               <h4>Dung hợp thú cưng</h4>\r\n               <div>\r\n                 <p>Là tính năng  kết hợp 2 loại thú cưng người chơi \r\nđang có thành một. Thú cưng nhận được sau khi  dung hợp thành công sẽ \r\nđược hỗ trợ linh tính, độ trưởng thành, tư chất, cấp ở mức  cao nhất của\r\n thú phụ.                 </p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung35.jpg\" height=\"325\" width=\"500\" /></p>\r\n                 <p>Sau khi dung  hợp, ngoại hình và các kỹ năng đang có của thú chính sẽ không bị thay đổi, còn  thú phụ sẽ bị mất đi.</p>\r\n                 <p><strong>Lưu ý:</strong> Đối với các\r\n loại thú cưng làm thú phụ  khi dung hợp là thú quý và có kèm theo kỹ \r\nnăng từ cấp trung trở lên, hệ thống sẽ  yêu cầu người chơi xác nhận \r\ntrước khi dung hợp.</p>\r\n</div>\r\n  <a></a>\r\n               <h4>Tu luyện thú cưng</h4>\r\n               <div>\r\n                 <p>Là tính năng  hỗ trợ người chơi huấn luyện thú cưng nhằm giúp thú cưng tăng nhanh kinh nghiệm.  Người chơi cần đến NPC Đơn Thu (Thái Cơ Thành 149,234) để có thể tiến hành tu  luyện.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung36.jpg\" height=\"301\" width=\"500\" /></p>\r\n                 <p>Người chơi  có thể lựa chọn\r\n kiểu tu luyện bằng đồng hoặc vàng, và có thể tăng tốc nhanh nhằm  giúp \r\nrút ngắn thời gian tu luyện cho tú cưng.<br />\r\n                 </p>\r\n<p><strong>Lưu ý:</strong> Thú cưng muốn tu luyện cần phải ở  trong trạng thái nhàn rỗi.              </p>\r\n            </div>\r\n             <a></a>\r\n               <h4>Kho thú cưng</h4>\r\n               <div>\r\n                 <p>Khi người  chơi có nhiều loại thú cưng, nhưng không muốn bỏ đi, có thể đến gặp NPC Uyển  Thu (Thái Cơ Thành 200,204) để cất bớt các loại thú cưng của mình vào kho và có  thể lấy ra bất cứ lúc nào mà không tốn phí.</p>\r\n                 <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/thucung/thucung37.jpg\" height=\"235\" width=\"500\" /></p>\r\n<p><strong>Lưu ý:</strong> Thú cưng đang trong trạng thái xuất  chiến không thể cất vào kho thú cưng.              </p>\r\n            </div>    ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 14:49:54', '50336', '2013-12-13 14:49:54', '0', '1');
INSERT INTO `letyii_article` VALUES ('19', 'Hệ thống Auto', '', '', 'Hệ thống Auto là một phần không thể thiếu trên chặng đường bôn tẩu của quý đồng đạo. Cùng khám phá xem hệ thống Auto có gì đặc sắc, giúp ích gì cho người chơi?\r\n                <p>Thông qua giao diện bản đồ nhỏ bên phải màn hình, nhấn vào nút <em><strong>Treo</strong></em> để mở hệ thống Auto:</p>\r\n                <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/auto/auto3.jpg\" height=\"199\" width=\"204\" /></p>\r\n                <p style=\"text-align:center\">Sau khi mở  hệ thống, nhân vật sẽ tự động tấn công quái đứng trong phạm vi nhất định</p>\r\n                <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/auto/auto1.jpg\" height=\"314\" width=\"302\" /></p>\r\n                <p>Nhấn vào  nút mở rộng để thiết lập thêm nhiều hỗ trợ từ Auto như nhặt đồ, tự thi triển kỹ  năng …</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/auto/auto2.jpg\" height=\"342\" width=\"450\" /></p>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 14:52:16', '50336', '2013-12-13 14:53:20', '0', '1');
INSERT INTO `letyii_article` VALUES ('20', 'Hệ thống Tiên cảnh', '', '', '<div>\r\n        <h4>Hệ thống tiên cảnh</h4>\r\n        <div>\r\n          <p>Tiên cảnh  là hệ thống quay đổi các vật phẩm\r\n quý hiếm trong Nghịch Tiên. Để có thể vào Tiên  cảnh, người \r\nchơi có thể chọn mục Tiên cảnh tại thanh hỗ trợ bên phái phía dưới  màn \r\nhình.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/hethongtiencanh/tiencanh1.jpg\" height=\"120\" width=\"500\" /></p>\r\n          <p style=\"text-align:center\">Phím tắt</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/hethongtiencanh/tiencanh2.jpg\" height=\"385\" width=\"500\" /></p>\r\n          <p>Có 2 loại  Tiên cảnh là Đáy biển và Bồng Lai. Người chơi có thể vào Tiên cảnh Bồng Lai, sẽ\r\n  có cơ hội nhận được các loại vật phẩm có tính chất và đẳng cấp cao hơn\r\n là Tiên  cảnh Đáy Biển, tuy nhiên sẽ hao tổn vàng gấp đôi Tiên cảnh Đáy\r\n Biển.</p>\r\n          <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/tinh-nang-pho-thong/hethongtiencanh/tiencanh3.jpg\" height=\"391\" width=\"500\" /></p>\r\n          <p style=\"text-align:center\">Tiên cảnh bồng lai</p>\r\n        </div>\r\n      </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-13 14:56:50', '1', '2013-12-16 11:11:32', '0', '1');
INSERT INTO `letyii_article` VALUES ('21', 'Tinh Luyện', '', '', '<div>\r\n          <h4> Tinh luyện trang bị</h4>\r\n<div>\r\n  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi16.jpg\" height=\"375\" width=\"500\" /></p>\r\n  <ul><li>Trang bị tím cấp 60 trở lên mới có  thể tinh luyện, tỉ lệ thành công 100%.</li><li>Trang bị tím tinh luyện sẽ trở thành  cam, giữ nguyên cấp độ cường hóa.</li><li>Sau khi tinh luyện giữ nguyên bảo  thạch và số lỗ, các thuộc tính cũng giữ nguyên.</li><li>Trang bị tinh luyện hoặc các nguyên  liệu là vật phẩm khoá, trang bị sau khi tinh luyện thành công cũng sẽ khoá.</li><li>Sau khi tinh luyện, thuộc tính kèm  tẩy luyện ban đầu sẽ không thay đổi.</li></ul>\r\n</div></div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 11:53:33', '50336', '2013-12-16 11:53:33', '0', '1');
INSERT INTO `letyii_article` VALUES ('22', 'Dung luyện trang bị', '', '', '<div>\r\n              <h4> Dung luyện trang bị</h4>\r\n<div>\r\n  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi15.jpg\" height=\"378\" width=\"500\" /></p>\r\n  <ul><li>Khi dung luyện các trang bị khác màu  thì sau khi dung luyện, trang bị nhận được sẽ giữ lại cấp độ của trang bị có  màu cao hơn.</li><li>Trang bị tham gia dung luyện phải từ  tím trở lên, cùng nghề, cùng vị trí, trang bị nhận được sẽ là khóa.</li><li>Các trang bị cùng màu nếu dung luyện  thì sau khi dung luyện, \r\ncấp độ, cấp độ cường hóa, phẩm chất sẽ lấy theo giá trị  cao nhất trong \r\ncác món đem đi dung luyện.</li><li>Sau khi dung luyện, các bảo thạch sẽ  được gỡ ra, số lỗ giữ nguyên.</li><li>Trang bị đổi trong NPC Trao Đổi Tiên  Sư sẽ không thể dung để dung luyện.</li></ul>\r\n</div>\r\n            </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 11:55:28', '50336', '2013-12-16 11:55:28', '0', '1');
INSERT INTO `letyii_article` VALUES ('23', 'Chuyển cường hóa', '', '', '<div>\r\n<h4> Chuyển cường hóa</h4>\r\n<div>\r\n  <p>Là tính năng chuyển cấp cường hoá từ  trang bị này sang một trang bị khác cùng vị trí:  </p>\r\n  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi14.jpg\" height=\"377\" width=\"500\" /></p>\r\n  <ul><li>Trang bị chuyển cường hóa phải +1  trở lên.</li><li>Trang bị chuyển cường hóa và trang  bị được nhận chuyển phải cùng nghề và cùng vị trí.</li><li>Trang bị chuyển cường hóa phải hơn  trang bị nhận chuyển 2 cấp cường hóa.</li><li>Trong quá trình chuyển có xác suất  rớt 1, 2 cấp cộng.</li><li>Nếu chuyên thành công thì trang bị  chuyển cường hóa không mất, cấp cường hóa trở về 0.</li><li>Khi chuyển thì sẽ giữ lại độ hoàn mỹ  cao nhất của trang bị được nhận chuyển.</li></ul>\r\n</div>\r\n          </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 11:57:06', '50336', '2013-12-16 11:57:06', '0', '1');
INSERT INTO `letyii_article` VALUES ('24', 'Tăng phẩm chất trang bị', '', '', '<div>\r\n          <h4> Tăng phẩm chất trang bị</h4>\r\n          <div>\r\n            <p>Tất cả trang bị xanh lam trở lên, không phải phẩm  chất hoàn mỹ đều có thể tăng phẩm chất, mỗi lần tăng cần một lượng Nguyệt Chi  Tinh Hoa nhất định.</p>\r\n            <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi12.jpg\" height=\"380\" width=\"500\" /></p>\r\n            <p>Nguyệt Chi Tinh Hoa có thể có được bằng  cách tham gia phó bản Thủ Hộ Linh Nguyên cấp 38 hoặc Hàng Ma Cổ Điện. Khi xuất  hiện Boss ở mỗi 10 đợt, người chơi đánh bại Boss sẽ có cơ hội nhận được.</p>\r\n            <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi13.jpg\" height=\"191\" width=\"186\" /></p>\r\n            <p>Trang  bị có phẩm chất khác nhau sẽ dùng các vật phẩm khác nhau để tăng phẩm chất lên  1 cấp:</p>\r\n            <ul><li>Từ thông thường lên  ưu tú cần 2 Nguyệt Chi Tinh Hoa sơ cấp.</li><li>Từ ưu tú lên tinh xảo cần 6 Nguyệt Chi Tinh Hoa  sơ cấp.</li><li>Từ tinh xảo lên trác việt cần 18 Nguyệt Chi Tinh  Hoa sơ cấp.</li><li>Từ trác việt lên hoàn mỹ cần 54 Nguyệt Chi Tinh  Hoa sơ cấp.</li></ul>\r\n            <p><strong>Quy tắc tăng phẩm chất trang bị:</strong></p>\r\n            <ul><li>\r\n                <p>Tất cả trang bị xanh lam trở lên, không phải hoàn mỹ và có  tiền tố đều có thể tăng phẩm chất.</p>\r\n              </li><li>\r\n                <p>Có thể dùng Nguyệt Chi Tinh Hoa để thăng phẩm chất trang bị.</p>\r\n              </li><li>\r\n                <p>Nếu trong quá trình thăng mà sử dụng nguyên liệu khóa thì trang bị nhận  được cũng sẽ khóa.</p>\r\n              </li><li>\r\n                <p>Trang bị sau khi thăng thì ngoài việc các thuộc tính \r\ncố định sẽ tăng  thì các trạng thái còn lại đều không đổi (đục lỗ, bảo \r\nthạch, cường hóa, các  thuộc tính thêm). </p>\r\n              </li></ul>\r\n          </div>\r\n          </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 11:58:23', '50336', '2013-12-16 11:58:23', '0', '1');
INSERT INTO `letyii_article` VALUES ('25', 'Dung hợp trang bị', '', '', '<div>\r\n      <h4> Dung hợp trang bị</h4>\r\n      <div>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi11.jpg\" height=\"376\" width=\"500\" /></p>\r\n        <ul><li>Người chơi chỉ có thể dung hợp trang  bị đúng hệ phái của mình.</li><li>Nguyên liệu dung hợp là 1 món trang bị xanh lam (trang bị \r\nmục tiêu) và 2 món trang bị  tím trở lên, 2 món trang bị này phải cùng \r\nmàu và cùng trong 1 bộ.</li><li>Trang bị màu xanh sẽ quyết  định vị trí của trang bị tím \r\nnhận được (Ví dụ: dung hợp trang bị nón xanh lam sẽ  nhận được nón tím, \r\nbộ trang bị tím sẽ quyết định màu và thuộc tính bộ của trang  bị tím \r\nnhận được) .</li><li>Nếu trong các nguyên liệu có trang  bị khóa thì trang bị nhận được cuối cùng cũng sẽ là khóa.</li></ul>\r\n      </div>\r\n    </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 14:36:50', '50336', '2013-12-16 14:36:50', '0', '1');
INSERT INTO `letyii_article` VALUES ('26', 'Phân giải trang bị', '', '', '<div>\r\n<h4> Tách trang bị</h4>\r\n<div>\r\n  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi10.jpg\" height=\"375\" width=\"500\" /></p>\r\n  <ul><li>Trang  bị trắng và xám không thể tách.</li><li>Tùy  vào màu sắc của trang bị phân giải mà sẽ nhận được các loại đá tẩy luyện khác  nhau.</li><li>1  trang bị sẽ phân giải được 1 đá tẩy luyện dùng để tẩy luyện trang bị.</li><li>Khi  phân giải trang bị hoàn mỹ sẽ có cơ hội nhận được bùa hoàn mỹ dùng để cường hóa  trang bị.</li></ul>\r\n</div>\r\n          </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 14:39:05', '50336', '2013-12-16 14:39:05', '0', '1');
INSERT INTO `letyii_article` VALUES ('27', 'Tiến giới trang bị', '', '', '<div>\r\n      <h4> Tiến giới trang bị</h4>\r\n      <div>\r\n        <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi9.jpg\" height=\"298\" width=\"395\" /></p>\r\n        <ul><li>Trang  bị tím trở lên mới có thể tiến giới, sau khi tiến giới sẽ tăng cấp độ và thuộc  tính của trang bị<br />\r\n          </li><li>\r\n            <p>Tiến giới  trang bị có xác suất thành công, dù thành công\r\n hay thất bại thì nguyên liệu  tiến giới đều sẽ mất, trang bị không mất.</p>\r\n          </li><li>\r\n            <p>Sử dụng nguyên liệu khóa để tiến giới trang bị chưa khóa thì trang bị sau  khi thao tác cũng sẽ bị khóa.</p>\r\n          </li><li>\r\n            <p> Mỗi lần tiến giới cần trả 1 lượng mảnh vỡ Phục  Ma - Tru Tiên - Thiên Tứ - Tru Thần\r\n tuỳ theo vật phẩm tiến giới và 1 lượng đá Huyền  Thiết. Khi tiến giới \r\nnếu có dùng thêm Bảo  Hộ Phù thì nếu thất bại cũng sẽ không bị mất Bách Niên Huyền Thiết.</p>\r\n          </li><li>\r\n            <p>Trang bị tím cấp 40 trở lên khi tiến giới thì cấp cường \r\nhoá và phẩm chất có  thể rớt. Trang bị cấp 40 trở xuống thì khi tiến \r\ngiới cấp cường hoá và phẩm chất  sẽ không rớt.</p>\r\n          </li><li>\r\n            <p>Khi tiến giới trang bị, có thể chọn tách đá quý và để vào hành trang.</p>\r\n          </li></ul>\r\n      </div>\r\n    </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 14:41:28', '50336', '2013-12-16 14:41:28', '0', '1');
INSERT INTO `letyii_article` VALUES ('28', 'Tẩy luyện trang bị', '', '', '<h4> Tẩy luyện trang bị</h4>\r\n                <div>\r\n                <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi5.jpg\" height=\"346\" width=\"456\" /></p>\r\n                  <p>Trang  bị tẩy luyện sẽ được tăng thêm một số thuộc tính kèm, tuỳ vào loại trang bị thuộc  tính nào.</p>\r\n                  <p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi6.jpg\" height=\"379\" width=\"198\" /></p>\r\n                  <p>Tẩy  luyện cần có đá tẩy luyện, trang bị phẩm\r\n chất nào thì cần phải có đá tẩy luyện  phẩm chất đó. Mỗi  lần tẩy luyện\r\n thì sẽ ra được các thuộc tính mới, nếu muốn giữ lại 1 thuộc tính  nào \r\nđó thì có thể dùng vật phẩm khóa tẩy luyện để giữ lại các thuộc tính \r\n(các  thuộc tính được chia thành 10 cấp sao, thuộc tính 10 sao là cao nhất). Giữ lại  càng nhiều thuộc tính tốn càng nhiều khoá tẩy luyện.</p>\r\n<p style=\"text-align:center\"><img src=\"http://static.onion.vn/tutien/images/source/he-thong-trang-bi/trangbi8.jpg\" height=\"263\" width=\"347\" /></p>\r\n              </div>\r\n                  <p><strong>Ghi chú</strong></p>\r\n                  <div>\r\n                      <ul><li>Trang  bị xanh lá trở lên mới có thể tẩy \r\nluyện, trang bị xanh lá tối đa có thể ra được  5 dòng thuộc tính, các \r\ntrang bị màu cao hơn tối đa có thể ra được 6 dòng thuộc  tính. </li><li>Trang  bị trắng không thể tẩy luyện để cộng thuộc tính kèm theo.</li><li>Số  lần tẩy luyện càng nhiều thì xác suất ra được thuộc tính nhiều sao sẽ càng cao.</li><li>Sử  dụng nguyên liệu đá tẩy luyện là vật phẩm khoá, vật phẩm sau khi tẩy luyện  thành công cũng sẽ khoá. </li></ul>\r\n                  </div>  ', '', '', '', null, null, null, null, null, '50336', '2013-12-16 14:42:55', '50336', '2013-12-16 14:42:55', '0', '1');

