DROP TABLE IF EXISTS `letyii_auth_item`;
CREATE TABLE `letyii_auth_item` (
  `name` varchar(64) NOT NULL,
  `type` int(11) NOT NULL,
  `description` text,
  `rule_name` varchar(64) DEFAULT NULL,
